import React from "react";
import { useNotesAPI } from "./useNotesAPI";
import { useState } from "react";
import { API_URL } from "../constants"

const NoteContext = React.createContext();

function NoteProvider(props){
  const {
    notes,
    saveNotes,
    loading,
    error,
    deleteNotes,
    addNotes,
    updateNotes,
  } = useNotesAPI([]);
  const [noteSelected, setNoteSelected] = useState(null);
  const [searchValue, setSearchValue] = React.useState("");
  const [openModal, setOpenModal] = React.useState(false);
  const archivedNotes = notes.filter(note => !!note.active);
  const totalNotes = notes.length;
  let searchedNotes = [];

  if (!searchValue.length >= 1){
    searchedNotes = archivedNotes
  } else {
    searchedNotes = notes.filter(note => {
      const noteText = note.title.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return noteText.includes(searchText)
    })
  }

  const completeNotes = (text) => {
    const noteIndex = notes.findIndex(note => note.text === text);
    const newNotes = [...notes];
    newNotes[noteIndex].completed = !(newNotes[noteIndex].completed);
    saveNotes(newNotes);
  }

  const noteSelectedItem = () => {
    if (noteSelected){
      return notes.find(note => note._id == noteSelected)
    } else {
      return null;
    }
  }

  const searchArchivedNotes = (archived) => {
    notes.filter(note => {
      return note.active = !archived
    })
  }

  return(
    <NoteContext.Provider value={{
      loading,
      error,
      totalNotes,
      archivedNotes,
      addNotes,
      searchValue,
      setSearchValue,
      searchedNotes,
      completeNotes,
      deleteNotes,
      openModal,
      setOpenModal,
      noteSelected,
      setNoteSelected,
      noteSelectedItem,
      updateNotes,
      searchArchivedNotes
    }}>
      {props.children}
    </NoteContext.Provider>
  )
}

export { NoteContext, NoteProvider };
