import React from "react";
import { useNotesAPI } from "./useNotesAPI";
import { useState } from "react";
import { API_URL } from "../constants"

const NoteContext = React.createContext();

function NoteProvider(props){
  const {
    notes,
    saveNotes,
    loading,
    error,
    deleteNotes,
    addNotes,
    updateNotes,
  } = useNotesAPI([]);
  const [searchArchived, setSearchArchived] = React.useState(false);
  const [noteSelected, setNoteSelected] = useState(null);
  const [searchValue, setSearchValue] = React.useState("");
  const [openModal, setOpenModal] = React.useState(false);
  const archivedNotes = notes.filter(note => !!note.active);

  const filteredNotes = () => {
    if (!searchValue.length >= 1){
      return archivedNotes
    } else {
      return notes.filter(note => {
        const noteText = note.title.toLowerCase();
        const searchText = searchValue.toLowerCase();
        return noteText.includes(searchText)
      })
    }
  }

  const totalNotes = filteredNotes().length;

  const noteSelectedItem = () => {
    if (noteSelected){
      return notes.find(note => note._id == noteSelected)
    } else {
      return null;
    }
  }

  return(
    <NoteContext.Provider value={{
      loading,
      error,
      totalNotes,
      archivedNotes,
      addNotes,
      searchValue,
      setSearchValue,
      deleteNotes,
      openModal,
      setOpenModal,
      noteSelected,
      setNoteSelected,
      noteSelectedItem,
      updateNotes,
      filteredNotes,
      searchArchived,
      setSearchArchived
    }}>
      {props.children}
    </NoteContext.Provider>
  )
}

export { NoteContext, NoteProvider };
