import React from "react";
import style from "./NoteItem.module.css";
import { NoteContext } from "../NoteContext";

function NoteItem(props){
    const {
        setNoteSelected
    } = React.useContext(NoteContext);

    const openForm = () => {
        setNoteSelected(props.id)
        props.setOpenModal()
    }

    return(
        <li className={style.NoteItem} key="{props._id}">
            <span className={`${style.Icon} ${style.IconCheck} ${(props.completed ? style.IconCheckActive : "")}`}
                onClick={props.onComplete}
            >
              âˆš
            </span>
            <p onClick={openForm} className={`${style.NoteItemP} ${(props.completed ? style.NoteItemPComplete : "")}`}>
                {props.text}
            </p>
            <span onClick={openForm}>Editar   </span>
            <span className={`${style.Icon} ${style.IconDelete}`}
                onClick={props.onDelete}
            >
                X
            </span>
        </li>
    );
}

export { NoteItem };
