import { useState } from "react";
import React from "react";
import { API_URL } from "../constants"

function useNotesAPI(initialValue){
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(true);
  const [notes, setNotes] = useState(initialValue);

  React.useEffect(() => {
    fetch(`${API_URL}/notes`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data)
        try {
          setNotes(data);
          setLoading(false);
        } catch(error) {
          setError(error);
        }
      });
  }, []);

  const saveNotes = (newItem) => {
    try {
    setNotes(newItem);
    } catch(error) {
      setError(error);
    }
  };

  const addNotes = (text) => {
    if (!text.trim()) {
      alert("La nota está vacía, escribe algo");
      return;
    }

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      "active": true,
      "title": text,
      "description": "jajaj2"
    });

    var requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };

    fetch(`${API_URL}/notes`, requestOptions)
      .then(response => response.text())
      .then(result => {
        const newNotes = [...notes];
        newNotes.push({
          completed: false,
          text,
        });
        saveNotes(newNotes);
      })
      .catch(error => console.log('error', error));



  }

  const deleteNotes = (id) => {
    var requestOptions = {
      method: 'DELETE'
    };

    fetch(`${API_URL}/notes/${id}`, requestOptions)
      .then(response => response.text())
      .then(result => {
        const noteIndex = notes.findIndex(note => note._id === id);
        const newNotes = [...notes];
        newNotes.splice(noteIndex, 1);
        saveNotes(newNotes);
      })
      .catch(error => console.log('error', error));
  }

  return {
    notes,
    saveNotes,
    loading,
    error,
    deleteNotes,
    addNotes
  };
}

export { useNotesAPI };
